import React, { Component } from 'react'
import axios  from 'axios'

const initialState={
    username:"",
    password:"",
    posts:[],
    usernamedb:"",
    passworddb:"", 


};


 class Admin extends Component {
    state= initialState ;

    validateposts= (posts) => {
        let usernamedb = "" ;
        let passworddb = "" ;
        let key = "" ;
        
        if (!posts.length) return null ; 
        posts.map((post,index) => {
            key=index ; 
            usernamedb = post.username ;
            passworddb = post.password ;
            this.setState({usernamedb,passworddb},);


        })  ;
       



        


        
        
    };
    
    handleChange= (e) => {
        this.setState({
           [e.target.name] : e.target.value ,
        })
    };
    handleSubmit =(e) => {
        e.preventDefault();
        
        
        const isValid =  this.validateposts(); 
        if (isValid){
            console.log(this.state);
            this.setState(initialState); }

       
    };
    getUser = () => {
        axios.get('/api')
          .then((response) => {
            const data = response.data;
            this.setState({ posts: data });
            console.log('Data has been received!!');
          })
          .catch(() => {
            alert('Error retrieving data!!!');
          });
      }
      
    render() {
        return (
            <div className="Form" style= {{marginLeft:"350px"}}>
               
        
                
            <form onSubmit={this.handleSubmit} className="col-md-12 ofset-md-2">
                <div>  
                    <label style={{fontSize:"15px"}}>
                    Ad Soyad
                    </label>
                </div>
                <div>    
                    <input type="text" name="username" value={this.state.username}  onChange={ this.handleChange } />
                    
                </div>
                
                <div>  
                    <label style={{fontSize:"15px"}}>
                   Sifre
                    </label>
                </div>
                
                <div>    
                    <input type="password" name="password" value={this.state.password} onChange={ this.handleChange}/ >
                    
                </div> 
                <div>
                <button style={{marginTop:"25px"}} Type="submit">Giriş</button>
                 </div>
                      
            </form> 
        </div>
        )
    }
}
export default Admin ;
